import { useState, useEffect } from 'react';
import { Image, Alert } from 'antd';
import axios from 'axios';
import { FileValidationResult, IMetaData } from '@/store/models/Files';
import MetaData from '@/components/ImageComponent/Metadata';

interface IImageComponent {
  transaction: string
}

const ImageComponent = ({transaction}: IImageComponent) => {
  const [file, setFile] = useState<Blob | undefined>(undefined);
  const [metaData, setMetaData] = useState<IMetaData|undefined>(undefined);
  const [validation, setValidation] = useState<FileValidationResult|undefined>(undefined);

  useEffect(() => {
    axios.get(`${process.env.NEXT_PUBLIC_API_URL}/download/${transaction}`, {
      method: 'GET',
      responseType: 'blob'
    }).then(response => {
      if (response.status === 200) {
        const data: Blob = response.data;
        setFile(data);
      }
    })
  }, []);

  useEffect(() => {
    axios.get(`${process.env.NEXT_PUBLIC_API_URL}/metadata/${transaction}`, {
      method: 'GET'
    }).then(response => {
      if (response.status === 200) {
        const data = response.data;
        setMetaData(data);
      }
    })
  }, []);

  useEffect(() => {
    if (file === undefined || metaData === undefined) {
      return;
    }
    
    const formData = new FormData();
    formData.append('file', file);
    formData.append('transaction', transaction.split('.')[0]);
    formData.append('metaData', JSON.stringify(metaData));

    axios.post('/api/filevalidation', formData).then(response => {
        setValidation(response.data);
    }).catch(err => {
        console.log(err);
    });
  }, [file, metaData]);

  const getAlert = () => {
    if (validation === undefined) {
      return null;
    }

    if (validation.imgCalculatedHash === validation.imgOriginalHash) {
      return <Alert
        message="File verified"
        description={validation.imgOriginalHash}
        type="success"
        showIcon={true} />
    } else {
      return <Alert
        message="File manipulated!"
        description={`According to the file hashes, this file was manipulated! Hash: ${validation.imgCalculatedHash} - Expected hash: ${validation.imgOriginalHash}`}
        type="warning"
        showIcon={true}
        />
    }
  }

  const getMetaDataAlert = () => {
    if (metaData === undefined) {
      return null;
    }

    if (validation?.metaDataOriginalHash === validation?.metaDataCalculatedHash) {
      return <Alert
        message="Metadata verified"
        type="success"
        showIcon={true}
        />
    } else {
      return <Alert
        message="Metadata manipulated"
        type="error"
        showIcon={true}
        />
    }
  }

  return (
    <div style={{ marginTop: '1rem', marginBottom: '1rem'}}>
      {getAlert()}
      <Image
        width={'100%'}
        src={file ? URL.createObjectURL(file) : undefined}
        fallback="data:image/png;base64,"
        />
        <p style={{ fontSize: '11px' }}>{transaction ? <a href={'https://explorer.testnet.aeternity.io/transactions/' + transaction.split('.')[0]}>Go to transaction</a> : 'No transaction available'}</p>
      <h5>Meta Data</h5>
      <div style={{ marginBottom: '1rem' }}>
        {getMetaDataAlert()}
      </div>
      {metaData ? <MetaData {...metaData} /> : null}
    </div>
  )
}

export default ImageComponent;
